import React from "react";

const pieceToEmoji: Record<string, string> = {
  K: "♔", Q: "♕", R: "♖", B: "♗", N: "♘", P: "♙",
  k: "♚", q: "♛", r: "♜", b: "♝", n: "♞", p: "♟︎",
  ".": "·",
};

type BoardGridProps = {
  board: string[][];
  selectedFrom: string | null;
  validMoves: string[];
  handleSquareClick: (r: number, c: number) => void;
  gameOver: boolean;
};

export function BoardGrid({ board, selectedFrom, validMoves, handleSquareClick, gameOver }: BoardGridProps) {
  function idxToSquare(r: number, c: number): string {
    const file = String.fromCharCode("a".charCodeAt(0) + c);
    const rank = String(8 - r);
    return `${file}${rank}`;
  }

  return (
    <div style={{
      border: "4px solid #8b4513",
      display: "grid",
      gridTemplateColumns: "repeat(8, 56px)",
      gridTemplateRows: "repeat(8, 56px)",
      userSelect: "none",
      boxShadow: "0 4px 12px rgba(0,0,0,0.3)",
    }}>
      {board.map((row, r) =>
        row.map((piece, c) => {
          const sq = idxToSquare(r, c);
          const isSelected = selectedFrom === sq;
          const isValidMove = validMoves.includes(sq);
          const bg = (r + c) % 2 === 0 ? "#f0d9b5" : "#b58863";
          let bgColor = bg;
          if (isSelected) bgColor = "#7fc97f";
          else if (isValidMove) bgColor = "#fdc689";

          return (
            <div key={`${r}-${c}`} onClick={() => handleSquareClick(r, c)}
              style={{
                width: 56, height: 56, display: "flex", alignItems: "center", justifyContent: "center",
                background: bgColor, fontSize: 32, cursor: gameOver ? "not-allowed" : "pointer",
                transition: "all 0.2s", border: isValidMove ? "2px solid #f90" : "none", position: "relative"
              }}
              title={sq}
            >
              {pieceToEmoji[piece] ?? piece}
              {isValidMove && piece === '.' && (
                <div style={{
                  position: "absolute", width: 14, height: 14, borderRadius: "50%",
                  background: "rgba(0,0,0,0.3)"
                }} />
              )}
            </div>
          );
        })
      )}
    </div>
  );
}
