import React, { useCallback, useState } from "react";
import { useAuth } from "../context/AuthContext";
import useChessSocket from "../context/useChessSocket";
import "./HomePage.css";

type GamePageProps = {
  roomId: string;
};

export default function GamePage({ roomId }: GamePageProps) {
  const { user } = useAuth();
  const [fen, setFen] = useState("");
  const [turn, setTurn] = useState("white");
  const [gameOver, setGameOver] = useState<string | null>(null);
  const [reason, setReason] = useState<string | undefined>(undefined);
  const [moveText, setMoveText] = useState("");
  const [chatText, setChatText] = useState("");
  const [chatLog, setChatLog] = useState<string[]>([]);

  const onUpdate = useCallback(
    (newFen: string, newTurn: string, newGameOver: string | null, r?: string) => {
      setFen(newFen);
      setTurn(newTurn);
      setGameOver(newGameOver);
      setReason(r);
    },
    []
  );

  const { connected, playerColor, sendMove, sendChat, leaveRoom } = useChessSocket(roomId, onUpdate);

  const handleSendMove = () => {
    if (!moveText) return;
    const ok = sendMove(moveText);
    if (!ok) {
      // optionally show error
      console.warn("sendMove failed");
    }
    setMoveText("");
  };

  const handleSendChat = () => {
    if (!chatText) return;
    const ok = sendChat(chatText);
    if (ok) {
      setChatLog((s) => [...s, `${user?.username || "You"}: ${chatText}`]);
      setChatText("");
    } else {
      console.warn("sendChat failed");
    }
  };

  const handleLeave = () => {
    try {
      leaveRoom();
    } finally {
      // fallback: reload so App returns to home
      window.location.reload();
    }
  };

  return (
    <div className="home-page">
      <header className="header">
        <div className="logo">♔ Chess Master</div>
        <div style={{ textAlign: "right" }}>
          <div className="username">{user?.username || "Người chơi"}</div>
          <div className="user-id">ID: {user?.user_id || "---"}</div>
        </div>
      </header>

      <section className="mode-card" style={{ maxWidth: 960, margin: "20px auto", display: "flex", gap: 20 }}>
        <div style={{ flex: 2 }}>
          <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 8 }}>
            <div>Room: <strong>{roomId}</strong></div>
            <div>
              {connected ? <span style={{ color: "#34d399" }}>Connected</span> : <span style={{ color: "#fca5a5" }}>Disconnected</span>}
              {" • "}
              Color: <strong>{playerColor || "—"}</strong>
            </div>
          </div>

          <div style={{ background: "#0b1220", padding: 12, borderRadius: 8, minHeight: 260, color: "#e5e7eb" }}>
            <div style={{ marginBottom: 8 }}>Turn: <strong>{turn}</strong></div>
            <div style={{ fontFamily: "monospace", whiteSpace: "pre-wrap" }}>{fen || "Board not available yet"}</div>
            {gameOver && (
              <div style={{ marginTop: 12, color: "#fecaca" }}>
                Game over — winner: <strong>{gameOver}</strong> {reason ? `(${reason})` : ""}
              </div>
            )}
          </div>

          <div style={{ display: "flex", gap: 8, marginTop: 12 }}>
            <input
              className="mode-input"
              style={{ flex: 1 }}
              placeholder="Nhập nước đi (ví dụ: e2e4)"
              value={moveText}
              onChange={(e) => setMoveText(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleSendMove()}
            />
            <button className="mode-btn mode-btn-primary" onClick={handleSendMove} disabled={!moveText}>
              Gửi nước đi
            </button>
            <button className="mode-btn" onClick={handleLeave} style={{ background: "#ef4444" }}>
              Thoát
            </button>
          </div>
        </div>

        <aside style={{ flex: 1 }}>
          <div style={{ marginBottom: 8, fontWeight: 600 }}>Chat</div>
          <div style={{ background: "#111827", padding: 8, borderRadius: 8, height: 220, overflowY: "auto" }}>
            {chatLog.length === 0 ? <div style={{ color: "#9ca3af" }}>Không có tin nhắn</div> : chatLog.map((c, i) => <div key={i} style={{ marginBottom: 6 }}>{c}</div>)}
          </div>

          <div style={{ display: "flex", gap: 8, marginTop: 8 }}>
            <input className="mode-input" style={{ flex: 1 }} placeholder="Gửi tin nhắn" value={chatText} onChange={(e) => setChatText(e.target.value)} onKeyDown={(e) => e.key === "Enter" && handleSendChat()} />
            <button className="mode-btn mode-btn-primary" onClick={handleSendChat} disabled={!chatText}>
              Gửi
            </button>
          </div>
        </aside>
      </section>
    </div>
  );
}
