// ...existing code...
import React, { useCallback, useEffect, useMemo, useState } from "react";
import { BoardGrid } from "./BoardGrid";
import { GameInfo } from "./GameInfo";
import useChessSocket from "../context/useChessSocket";

type BoardProps = { roomId: string };

export default function Board({ roomId }: BoardProps) {
  const [fen, setFen] = useState<string>("................................................................................................................................");
  const [turn, setTurn] = useState<string>("white");
  const [gameOver, setGameOver] = useState<string | null>(null);
  const [reason, setReason] = useState<string | undefined>(undefined);
  const [selectedFrom, setSelectedFrom] = useState<string | null>(null);
  const [validMoves, setValidMoves] = useState<string[]>([]);
  const [opponentConnected, setOpponentConnected] = useState<boolean>(false);

  const onUpdate = useCallback((newFen: string, newTurn: string, over: string | null, r?: string) => {
    if (newFen) setFen(newFen);
    setTurn(newTurn);
    setGameOver(over);
    setReason(r);
    setSelectedFrom(null);
    setValidMoves([]);
    setOpponentConnected(over ? true : true); // if state arrives assume opponent present
  }, []);

  const { connected, playerColor, sendMove, leaveRoom } = useChessSocket(roomId, onUpdate);

  useEffect(() => {
    // when connected and no FEN yet, waiting for opponent
    if (connected) {
      // nothing special for now
    }
  }, [connected]);

  const board = useMemo(() => {
    const parts = fen && fen.includes("/") ? fen.split("/") : [];
    let rows: string[][] = [];
    if (parts.length === 8) rows = parts.map(row => row.split(""));
    else if (fen.length >= 64) {
      rows = [];
      for (let r = 0; r < 8; r++) {
        rows.push(fen.slice(r * 8, r * 8 + 8).split(""));
      }
    } else {
      rows = Array.from({ length: 8 }, () => Array(8).fill("."));
    }
    return rows;
  }, [fen]);

  function idxToSquare(r: number, c: number): string {
    const file = String.fromCharCode("a".charCodeAt(0) + c);
    const rank = String(8 - r);
    return `${file}${rank}`;
  }

  function handleSquareClick(r: number, c: number) {
    if (gameOver) return;
    const sq = idxToSquare(r, c);

    // select first
    if (!selectedFrom) {
      setSelectedFrom(sq);
      // TODO: compute valid moves if you have a rules engine
      setValidMoves([]);
      return;
    }

    // if re-click same square -> deselect
    if (selectedFrom === sq) {
      setSelectedFrom(null);
      setValidMoves([]);
      return;
    }

    // send move as "e2e4" style
    const moveStr = `${selectedFrom}${sq}`;
    const ok = sendMove(moveStr);
    if (!ok) {
      // optionally show error -> omitted for brevity
    }
    setSelectedFrom(null);
    setValidMoves([]);
  }

  return (
    <div style={{ display: "flex", gap: 20, alignItems: "flex-start", padding: 12 }}>
      {/* Left: board */}
      <div style={{
        flex: "0 0 auto",
        width: 56 * 8,
        boxShadow: "0 6px 18px rgba(0,0,0,0.2)",
        borderRadius: 8,
        overflow: "hidden",
      }}>
        <BoardGrid
          board={board}
          selectedFrom={selectedFrom}
          validMoves={validMoves}
          handleSquareClick={handleSquareClick}
          gameOver={!!gameOver}
        />
      </div>

      {/* Right: game / player info */}
      <div style={{ width: 320 }}>
        <GameInfo
          turn={turn}
          gameOver={gameOver}
          reason={reason}
          opponentConnected={opponentConnected}
          playerColor={playerColor}
        />

        <div style={{ marginTop: 12, display: "flex", gap: 8 }}>
          <button
            onClick={() => leaveRoom()}
            style={{
              padding: "8px 12px",
              background: "#ef4444",
              color: "white",
              border: "none",
              borderRadius: 6,
              cursor: "pointer"
            }}
          >
            Rời phòng
          </button>

          <div style={{ flex: 1, alignSelf: "center", color: "#6b7280" }}>
            {connected ? <span style={{ color: "#10b981" }}>● Đã kết nối</span> : <span style={{ color: "#f59e0b" }}>● Đang kết nối...</span>}
            <div style={{ marginTop: 6 }}>
              Bạn: {playerColor === "white" ? "⚪ Trắng" : playerColor === "black" ? "⚫ Đen" : "—"}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
