import React, { useEffect, useMemo, useState } from "react";
import useChessSocket from "../context/useChessSocket";
import { ChessRules } from "./chessRules";

type BoardProps = {
  roomId: string;
};

const pieceToEmoji: Record<string, string> = {
  K: "‚ôî", Q: "‚ôï", R: "‚ôñ", B: "‚ôó", N: "‚ôò", P: "‚ôô",
  k: "‚ôö", q: "‚ôõ", r: "‚ôú", b: "‚ôù", n: "‚ôû", p: "‚ôüÔ∏é",
  ".": "¬∑",
};

export default function Board({ roomId }: BoardProps) {
  const [fen, setFen] = useState<string>("................................................................");
  const [turn, setTurn] = useState<string>("white");
  const [gameOver, setGameOver] = useState<string | null>(null);
  const [reason, setReason] = useState<string | undefined>(undefined);
  const [selectedFrom, setSelectedFrom] = useState<string | null>(null);
  const [validMoves, setValidMoves] = useState<string[]>([]);
  const [errorMsg, setErrorMsg] = useState<string>("");

  const onUpdate = (newFen: string, newTurn: string, over: string | null, r?: string) => {
    if (newFen) setFen(newFen);
    setTurn(newTurn);
    setGameOver(over);
    setReason(r);
    setSelectedFrom(null);
    setValidMoves([]);
  };

  const { connected, playerColor, sendMove, leaveRoom } = useChessSocket(roomId, onUpdate);

  useEffect(() => {
    return () => {
      try {
        leaveRoom();
      } catch {}
    };
  }, []);

  // Parse FEN th√†nh m·∫£ng 2D
  const board = useMemo(() => {
    const parts = fen && fen.includes("/") ? fen.split("/") : [];
    let rows: string[][] = [];

    if (parts.length === 8) {
      rows = parts.map(row => row.split(""));
    } else if (fen.length >= 64) {
      for (let i = 0; i < 8; i++) {
        rows.push(fen.slice(i * 8, i * 8 + 8).split(""));
      }
    } else {
      rows = Array.from({ length: 8 }, () => Array(8).fill("."));
    }

    return rows;
  }, [fen]);

  function idxToSquare(r: number, c: number): string {
    const file = String.fromCharCode("a".charCodeAt(0) + c);
    const rank = String(8 - r);
    return `${file}${rank}`;
  }

  function handleSquareClick(r: number, c: number) {
    const sq = idxToSquare(r, c);

    // N·∫øu game ƒë√£ k·∫øt th√∫c
    if (gameOver) {
      setErrorMsg("Tr·∫≠n ƒë·∫•u ƒë√£ k·∫øt th√∫c!");
      return;
    }

    // N·∫øu kh√¥ng ph·∫£i l∆∞·ª£t c·ªßa m√¨nh
    if (playerColor !== turn) {
      setErrorMsg("Ch∆∞a ƒë·∫øn l∆∞·ª£t c·ªßa b·∫°n!");
      return;
    }

    // Ch∆∞a ch·ªçn √¥ n√†o
    if (!selectedFrom) {
      const piece = board[r][c];
      if (!ChessRules.isPlayerPiece(piece, playerColor as 'white' | 'black')) {
        setErrorMsg("H√£y ch·ªçn qu√¢n c·ªù c·ªßa b·∫°n!");
        return;
      }

      setSelectedFrom(sq);
      setErrorMsg("");

      // T√≠nh c√°c n∆∞·ªõc ƒëi h·ª£p l·ªá (preview)
      const possible: string[] = [];
      for (let toR = 0; toR < 8; toR++) {
        for (let toC = 0; toC < 8; toC++) {
          const toSq = idxToSquare(toR, toC);
          const fromPos = ChessRules.notationToPosition(sq);
          const toPos = ChessRules.notationToPosition(toSq);

          if (fromPos && toPos) {
            const validation = ChessRules.isValidMove(
              board,
              { from: fromPos, to: toPos },
              playerColor as 'white' | 'black'
            );
            if (validation.valid) {
              possible.push(toSq);
            }
          }
        }
      }
      setValidMoves(possible);
      return;
    }

    // ƒê√£ ch·ªçn √¥, click l·∫°i c√πng √¥ th√¨ b·ªè ch·ªçn
    if (selectedFrom === sq) {
      setSelectedFrom(null);
      setValidMoves([]);
      return;
    }

    // Th·ª±c hi·ªán n∆∞·ªõc ƒëi
    const fromPos = ChessRules.notationToPosition(selectedFrom);
    const toPos = ChessRules.notationToPosition(sq);

    if (!fromPos || !toPos) {
      setErrorMsg("V·ªã tr√≠ kh√¥ng h·ª£p l·ªá!");
      return;
    }

    // Validate ph√≠a client
    const validation = ChessRules.isValidMove(
      board,
      { from: fromPos, to: toPos },
      playerColor as 'white' | 'black'
    );

    if (!validation.valid) {
      setErrorMsg(validation.error || "N∆∞·ªõc ƒëi kh√¥ng h·ª£p l·ªá!");
      setSelectedFrom(null);
      setValidMoves([]);
      return;
    }

    // G·ª≠i n∆∞·ªõc ƒëi l√™n server
    const ok = sendMove(`${selectedFrom}${sq}`);
    if (!ok) {
      setErrorMsg("Kh√¥ng th·ªÉ g·ª≠i n∆∞·ªõc ƒëi!");
    }

    setSelectedFrom(null);
    setValidMoves([]);
    setErrorMsg("");
  }

  return (
    <div style={{ fontFamily: "sans-serif", maxWidth: 800, margin: "0 auto", padding: 16 }}>
      {/* Header */}
      <div style={{ 
        display: "flex", 
        justifyContent: "space-between", 
        marginBottom: 16,
        padding: 12,
        background: "#f5f5f5",
        borderRadius: 8
      }}>
        <div>
          <strong>Ph√≤ng:</strong> {roomId}{" "}
          <span style={{ marginLeft: 16, color: connected ? "green" : "red" }}>
            ‚óè {connected ? "ƒê√£ k·∫øt n·ªëi" : "M·∫•t k·∫øt n·ªëi"}
          </span>
        </div>
        <div>
          <strong>M√†u qu√¢n:</strong> {playerColor === "white" ? "‚ö™ Tr·∫Øng" : playerColor === "black" ? "‚ö´ ƒêen" : "üëÅÔ∏è Kh√°n gi·∫£"}
        </div>
      </div>

      {/* Error Message */}
      {errorMsg && (
        <div style={{ 
          padding: 8, 
          marginBottom: 12, 
          background: "#fee", 
          border: "1px solid #fcc",
          borderRadius: 4,
          color: "#c00"
        }}>
          ‚ö†Ô∏è {errorMsg}
        </div>
      )}

      <div style={{ display: "flex", gap: 24 }}>
        {/* B√†n c·ªù */}
        <div>
          <div style={{
            border: "4px solid #8b4513",
            display: "grid",
            gridTemplateColumns: "repeat(8, 50px)",
            gridTemplateRows: "repeat(8, 50px)",
            userSelect: "none",
            boxShadow: "0 4px 12px rgba(0,0,0,0.3)"
          }}>
            {board.map((row, r) =>
              row.map((piece, c) => {
                const sq = idxToSquare(r, c);
                const isSelected = selectedFrom === sq;
                const isValidMove = validMoves.includes(sq);
                const bg = (r + c) % 2 === 0 ? "#f0d9b5" : "#b58863";

                let bgColor = bg;
                if (isSelected) bgColor = "#7fc97f";
                else if (isValidMove) bgColor = "#fdc689";

