import React from "react";
import { useAuth } from "../context/AuthContext";
import Broad from "../Broad/broad";
import "./HomePage.css";

type GamePageProps = {
  roomId: string;
};

export default function GamePage({ roomId }: GamePageProps) {
  const { user } = useAuth();

  return (
    <div className="home-page">
      <header className="header">
        <div className="logo">♔ Chess Master</div>
        <div style={{ textAlign: "right" }}>
          <div className="username">{user?.username || "Người chơi"}</div>
          <div className="user-id">ID: {user?.user_id || "---"}</div>
        </div>
      </header>

      <section style={{ maxWidth: 960, margin: "20px auto" }}>
        <Broad roomId={roomId} />
      </section>
    </div>
  );
}
